svg.script:
window.onload = function() {
  var canvas = new SVGCanvasElement(0,0,300,300,
    document.getElementById('groupBelowMap')
  );
  var context = canvas.getContext('2d');
  context.drawImage(
    canvas.importImage('svgImage'),0,0,canvas.width,canvas.height
  );
  var hint = document.getElementById('svgHint');
  var audio = document.getElementById('foAudio');
  var sounds = {};
  var vector = document.getElementById('svgVector');
  var lastkey = null;
  var ogg = {
    '230,0,77'    : 'fs_city.ogg',      // settlement areas
    '255,255,0'   : 'fs_sprinkler.ogg', // arable Land
    '230,128,0'   : 'fs_sprinkler.ogg', // permanent Crops
    '166,242,0'   : 'fs_cowbells.ogg',  // meadows and pastures
    '0,166,0'     : 'fs_trees.ogg',     // forrests
    '204,242,77'  : 'fs_cowbells.ogg',  // natural grassland
    '230,230,230' : 'fs_wind.ogg',      // wasteland
    '166,230,204' : 'fs_wind.ogg',      // glaciers
    '166,166,255' : 'fs_frogs.ogg',     // wetlands
    'lake'        : 'fs_waves.ogg',
    'stream'      : 'fs_stream.ogg',
    'river'       : 'fs_river.ogg',
    'creek'       : 'fs_creek.ogg',
    'road'        : 'fs_cars.ogg',
    'rail'        : 'fs_train.ogg',
    'alp'         : 'mt_lamparti_polka.ogg',
    'hut'         : 'mt_radsberg_polka.ogg',
    'castle'      : 'fs_fanfare.ogg',
    'church'      : 'fs_bells.ogg'
  };
  var taNS = 'http:/tirolatlas.uibk.ac.at/svg/ns';

  // check for audio
  if (typeof audio.play == 'undefined') {
    hint.firstChild.nodeValue = 'Your browser does not support HTMLAudioElement';
    hint.parentNode.setAttributeNS(null,"visibility","visible");
    return;
  }

  var _stopAudio = function() {
    try {
        sounds[lastkey].volume = 0.0;
        lastkey = null;
    }
    catch(e) {};
  };
  var _playAudio = function(key) {
    if (key != lastkey) {
      _stopAudio();
      if (ogg[key]) {
        if (! sounds[key]) {
          var snd = audio.cloneNode(true);
          snd.setAttribute("src","audio/"+ogg[key]);
          sounds[key] = audio.parentNode.appendChild(snd);
        }
        sounds[key].volume = 1.0;
        sounds[key].play();
      }
      lastkey = key;
    }
  };

  vector.onmousemove = function(evt) {
    if (evt.target.hasAttributeNS(taNS,"layer")) {
      _playAudio(evt.target.getAttributeNS(taNS,"layer"));
    }
  };
  vector.onmouseout = function(evt) {
    _stopAudio();
  };
  context.canvas.onmousemove = function(evt)  {
    var px = context.getImageData(evt.clientX,evt.clientY,1,1).data;
    _playAudio(px[0]+','+px[1]+','+px[2]);
  };
  context.canvas.onmouseout = function(evt) {
    _stopAudio();
  };

  // and a hidden video ...
  var camera = document.getElementById('svgCamera');
  var video = document.getElementById('foVideo')
  camera.onmouseover = function() {
    video.parentNode.parentNode.setAttributeNS(null,"display","inline");
    _playAudio('hut');
    video.play();
  };
  camera.onmouseout = function() {
    _stopAudio();
    video.parentNode.parentNode.setAttributeNS(null,"display","none");
    video.currentTime = 0;
  };
  addNavigation();
};
